(function(win){
  var Velocity = function(asts){
    this.asts = asts;
    this.init();
  };
  Velocity.Helper = {};
  Velocity.prototype = {
    constructor: Velocity
  };
  
  var guid = 0;
  //api map
  var utils = {
    forEach : function(data, cb, host) {
    	data && data.length && _.each(data, function(d, index) {
		    cb.apply(host, arguments);
		});
    },
    some    : _.some,
    mixin   : jQuery.extend,
    guid    : function() {
    	guid++;
	    return "velocity-1111-2222" + guid;
    },
    isArray : jQuery.isArray,
    indexOf : jQuery.inArray,
    // 1.2没有keys方法，考虑独立utils
    keys    : _.keys,
    now     : jQuery.now
  };

  {helper}
  {velocity}
  win.Velocity = Velocity;
})(this);
